$(document).ready(function () {
    $(document).on("scroll", onScroll);
    
    $(\'a[href^="#"]\').on(\'click\', function (e) {
        e.preventDefault();
        $(document).off("scroll");
        
        $(\'a\').each(function () {
            $(this).removeClass(\'active\');
        })
        $(this).addClass(\'active\');
      
        var target = this.hash,
            menu = target;
        $target = $(target);
        /*$(\'html, body\').stop().animate({
            \'scrollTop\': $target.offset().top - 100
        }, 500, \'swing\', function () {
            window.location.hash = target;
            $(document).on("scroll", onScroll);
        });*/
           $("html, body").animate({ scrollTop: $($(this).attr("href")).offset().top - 100 }, 500); /* javir-  скролить в тор при відкритті мапи */
    });
});

function onScroll(event){
    var scrollPos = $(document).scrollTop();
    $(\'.contents-pages a\').each(function () {
        var currLink = $(this);
        var refElement = $(currLink.attr("href"));
        if (refElement.position().top <= scrollPos && refElement.position().top + refElement.height() > scrollPos) {
            $(\'.contents-pages ul li a\').removeClass("active");
            currLink.addClass("active");
        }
        else{
            currLink.removeClass("active");
        }
    });
}

    function onScroll(event){
        var scrollPos = $(document).scrollTop();
        $(\'.contents-pages a\').each(function () {
            var currLink = $(this);
            var refElement = $(currLink.attr("href"));
            if (refElement.position().top <= scrollPos && refElement.position().top + refElement.height() > scrollPos) {
                $(\'.contents-pages ul li a\').removeClass("active");
                currLink.addClass("active");
            }
            else{
                currLink.removeClass("active");
            }
        });
    };   




    if (($(window).width() > 768)){
        $(function(){
            var a = document.querySelector(\'.fixed-block\'), b = null, P = 100;

            if(a !== null) {
                window.addEventListener( \'scroll\', Ascroll, false );
                document.body.addEventListener( \'scroll\', Ascroll, false );
            }
            function Ascroll() {
                if (b == null) {
                    var Sa = getComputedStyle(a, \'\'), s = \'\';
                    for (var i = 0; i < Sa.length; i++) {
                        if (Sa[i].indexOf(\'overflow\') == 0 || Sa[i].indexOf(\'padding\') == 0 || Sa[i].indexOf(\'border\') == 0 || Sa[i].indexOf(\'outline\') == 0 || Sa[i].indexOf(\'box-shadow\') == 0 || Sa[i].indexOf(\'background\') == 0) {
                            s += Sa[i] + \': \' +Sa.getPropertyValue(Sa[i]) + \'; \'
                        }
                    }
                    b = document.createElement(\'div\');
                    b.style.cssText = s + \' box-sizing: border-box; width: \' + a.offsetWidth + \'px;\';
                    a.insertBefore(b, a.firstChild);
                    var l = a.childNodes.length;
                    for (var i = 1; i < l; i++) {
                        b.appendChild(a.childNodes[1]);
                    }
                    a.style.height = b.getBoundingClientRect().height + \'px\';
                    a.style.padding = \'0\';
                    a.style.border = \'0\';
                }
                var Ra = a.getBoundingClientRect(),
                    R = Math.round(Ra.top + b.getBoundingClientRect().height - document.querySelector(\'.fixed-block-stop\').getBoundingClientRect().bottom);
                if ((Ra.top - P) <= 0) {
                    if ((Ra.top - P) <= R) {
                        b.className = \'stop\';
                        b.style.top = - R +\'px\';
                    } else {
                        b.className = \'sticky\';
                        b.style.top = P + \'px\';
                    }
                } else {
                    b.className = \'\';
                    b.style.top = \'\';
                }
                window.addEventListener(\'resize\', function() {
                    a.children[0].style.width = getComputedStyle(a, \'\').width
                }, false);
            }
        });
    }

</script>
